const lassoLoader = require("lasso-loader");
module.exports = {
    load: function () {
        return new Promise((resolve, reject) => {
            lassoLoader.async(function (err) {
                if (err) reject(err);
                else
                    resolve(
                        require("@google/model-viewer/dist/model-viewer-umd.min.js"),
                    );
            });
        });
    },
};
